function a(){$(".glowAnime").each(function(){var n=$(this).offset().top-50,i=$(window).scrollTop(),o=$(window).height();i>=n-o?$(this).addClass("glow"):$(this).removeClass("glow")})}$(window).on("load",function(){var n=$(".glowAnime");n.each(function(){var i=$(this).text(),o="";i.split("").forEach(function(e,s){if(e!==" ")if(s<10)o+='<span style="animation-delay:.'+s+'s;">'+e+"</span>";else{var t=s/10;o+='<span style="animation-delay:'+t+'s;">'+e+"</span>"}else o+=e}),$(this).html(o)}),a()});
